import React from 'react';
import c from './Dialogs.module.css';


const Message = (props) => {
    return (
        <div className={c.dialog}>{props.msg}</div>
    );
}


let dialogData = [
    { id: 1, name: 'Danon' },
    { id: 2, name: 'Vanon' },
    { id: 3, name: 'Vlad' },
    { id: 4, name: 'Vitalya'}
]



const DialogItem = (props) =>{
    return (
        <div>
            {props.name}
        </div>
    )
}

    let dialogElements = dialogData
    .map( (dialog) => <DialogItem name={dialog.name} id={dialog.id} /> );


   const Dialogs = () => {
    return (
        <div className={c.dialogs}>
            <div className={c.dialogs_item}>
                { dialogElements }
            </div>

            <div>
                <Message msg='Hi' />
                <Message msg='How are u?' />
                <Message msg='I`m ok' />
            </div>
        </div>
    );
}

export default Dialogs;
